// Modal with hidden actions
export const HiddenActions: Story = {
  name: "Hidden Actions",
  parameters: {
    // Auto-generated by sync-storybook-zephyr - do not add manually
    zephyr: { testCaseId: "SW-T821" },
  },
  args: {
    ...Default.args,
    hideActions: true,
  },
};
const InteractiveComponent = () => {
  const [isOpen, setIsOpen] = useState(false);

  const handleOpen = () => {
    setIsOpen(true);
  };

  const handleClose = () => {
    setIsOpen(false);
  };

  const handleConfirm = () => {
    console.log("Reset confirmed");
    setIsOpen(false);
  };

  return (
    <div>
      <Button onClick={handleOpen} variant="primary">
        Open Modal
      </Button>
      <Modal isOpen={isOpen} onClose={handleClose} onConfirm={handleConfirm} title="Title">
        <p>
          Are you sure you want to reset this view? you will need to input all the settings and load all the graph from
          the start
        </p>
      </Modal>
    </div>
  );
};

