// Interactive example
const InteractiveWithThemeComponent = () => {
  const [isOpen, setIsOpen] = useState(false);

  const handleOpen = () => {
    setIsOpen(true);
  };

  const handleClose = () => {
    setIsOpen(false);
  };

  const handleConfirm = () => {
    console.log("Reset confirmed");
    setIsOpen(false);
  };

  return (
    <ThemeProvider
      theme={{
        colors: {
          primary: "#DC2626",
          primaryHover: "#B91C1C",
          primaryActive: "#991B1B",
          background: "#FEE2E2",
        },
        radius: {
          large: "8px",
          medium: "8px",
        },
      }}
    >
      <div>
        <Button onClick={handleOpen} variant="primary">
          Open Themed Modal
        </Button>
        <Modal isOpen={isOpen} onClose={handleClose} onConfirm={handleConfirm} title="Themed Modal">
          <p>This modal and button are both themed with custom red colors and sharp corners!</p>
        </Modal>
      </div>
    </ThemeProvider>
  );
};

